import { useEffect, useState, useContext } from "react"
import { CartContext } from "../Components.js/CartContext"

const Cart = () => {

  const { cart } = useContext(CartContext)
  const [products, setProduct] = useState([])

  useEffect(() => {
    if (!cart.items) {
      return;
    }
//  console.log('cart',Object.keys(cart.items))
    fetch('/api/products', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
       
      },
      body: JSON.stringify({ ids: Object.keys(cart.items) })
    }).then(res =>

      res.json())
           
      .then(products => {
        setProduct(products);

      })



  }, [cart])


  return (

    <>

      <div className="container mx-auto lg:w-1/2 w-full pb-24">
        <h1 className="my-12 font-bold">Cart items</h1>
        <ul>
          {
            products.map(product=>{
              return(
                <li className="mb-12">
                <div className="flex items-center justify-between">
                  <div className="flex items-center">
                    <img className="h-16" src="/images/peproni.png" alt="" />
                    <span className="font-bold ml-4 w-48"> pizza1</span>
                  </div>
                  <div>
                    <button className="bg-yellow-500 px-4 py-2 rounded-full leading-none">-</button>
                    <b className='px-4'>2</b>
                    <button className="bg-yellow-500 px-4 py-2 rounded-full leading-none">+</button>
                  </div>
                  <span>₹ 100</span>
                  <button className="bg-red-500 px-4 py-2 rounded-full leading-none text-white">Delete</button>
                </div>
              </li>
              )
            })

          }
        
        </ul>
        <hr className="my-6" />
        <div className="text-right">
          <b>Grand Total:</b> ₹
        </div>
        <div className="text-right mt-6">
          <button className="bg-yellow-500 px-4 py-2 rounded-full leading-none">Order Now</button>
        </div>
      </div>
    </>

  )
}

export default Cart
